WITH VentasPorVendedor AS (
  SELECT vendedor_id, SUM(total) as suma
  FROM ventas GROUP BY vendedor_id
),
VendedoresTop AS (
  SELECT vendedor_id FROM VentasPorVendedor WHERE suma > 1000
)
SELECT v.nombre FROM vendedores v 
JOIN VendedoresTop vt ON v.id = vt.vendedor_id;

